import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";import{e as p}from"./app.c088cce8.js";const o={},a=p('<h1 id="\u7B2C\u4E00\u6B21\u53BB\u5B66\u6821" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u6B21\u53BB\u5B66\u6821" aria-hidden="true">#</a> \u7B2C\u4E00\u6B21\u53BB\u5B66\u6821</h1><h2 id="\u5B5F\u8425\u5C0F\u5B66\u95E8\u53E3" tabindex="-1"><a class="header-anchor" href="#\u5B5F\u8425\u5C0F\u5B66\u95E8\u53E3" aria-hidden="true">#</a> \u5B5F\u8425\u5C0F\u5B66\u95E8\u53E3</h2><p>\u6211\u4E0A\u5B66\u6BD4\u8F83\u65E9,\u5DEE\u4E0D\u591A\u4E94\u5C81\u534A\u5C31\u53BB\u4E0A\u5B66\u4E86,\u90A3\u65F6\u5019\u8FD8\u7279\u522B\u6015\u751F.\u7B2C\u4E00\u6B21\u53BB\u5B5F\u8425\u5C0F\u5B66\u8FD8\u662F\u6211\u5988\u5988\u9A91\u7740&quot;\u51E4\u51F0&quot;\u724C\u81EA\u884C\u8F66\u5E26\u6211\u53BB\u7684,\u521A\u5230\u6821\u95E8\u53E3,\u4FBF\u9047\u5230\u4E86\u90BB\u5C45\u4EEC.</p><p>&quot;\u54A6,\u8FD9\u4E0D\u662F\u9E3D\u5B50\u5417?\u4E5F\u9001\u5B69\u5B50\u6765\u4E0A\u5B66?\u4ED6\u8FD8\u662F\u4E2A\u5C0F\u4E0D\u70B9\u5462&quot;,\u6211\u4E00\u770B,\u539F\u6765\u662F\u5706\u5706\u5988\u5988,\u5979\u4E5F\u6B63\u5E26\u7740\u5979\u7684\u5973\u513F\u5706\u5706\u6765\u4E0A\u5B66.</p><p>&quot;\u662F\u554A,\u65E9\u70B9\u5B66\u77E5\u8BC6,\u4E0D\u4F1A\u50CF\u6211\u4EEC\u4E00\u6837\u8FD8\u5F97\u4E0B\u5730\u5E72\u6D3B,\u7D2F\u6B7B\u7D2F\u6D3B\u4E5F\u8D5A\u4E0D\u4E86\u591A\u5C11\u94B1.&quot;,\u6211\u5988\u8BF4.</p><p>&quot;\u90A3\u8FB9\u597D\u50CF\u662F\u4ED6\u4EEC\u73ED\u4E3B\u4EFB,\u6211\u5148\u8FC7\u53BB\u4E86\u54C8&quot;,\u6211\u5988\u8BF4.</p><p>&quot;\u90A3\u4F60\u5148\u53BB,\u4ED6\u4EEC\u73ED\u4E3B\u4EFB\u8FD8\u6CA1\u6765,\u6211\u5F97\u518D\u7B49\u4F1A\u4E8C&quot;,\u5706\u5706\u5988\u8BF4.</p><p>\u6211\u5988\u548C\u6211\u4E00\u8D77\u5230\u4E86\u6559\u5BA4\u95E8\u524D.</p><p>&quot;\u521A\u4E0A\u5B66\u662F\u5427?\u5148\u767B\u8BB0\u4E00\u4E0B&quot;,\u90A3\u4F4D\u8001\u5E08\u770B\u7740\u6211,\u773C\u91CC\u5145\u6EE1\u4E86\u6148\u7231.</p><p>&quot;\u8FD9\u5B69\u5B50\u8FD9\u4E48\u5C0F\u5C31\u6765\u4E0A\u5B66\u4E86\u554A,\u4E0D\u5728\u5BB6\u91CC\u591A\u73A9\u4E24\u5929?&quot;,\u8001\u5E08\u8BF4.&quot;\u4EE5\u540E\u6211\u5C31\u662F\u4ED6\u4EEC\u7684\u73ED\u4E3B\u4EFB\u4E86,\u6709\u4E8B\u60C5\u5C31\u6765\u627E\u6211&quot;</p><p>&quot;\u90A3\u8FD9\u5B69\u5B50\u5C31\u6258\u4ED8\u7ED9\u60A8\u4E86\u54C8&quot;,\u5988\u5988\u8F7B\u58F0\u8BF4\u9053.</p><p>&quot;\u4F60\u5C31\u53BB\u5FD9\u5427,\u8FD9\u91CC\u6211\u7BA1\u7740\u5462&quot;,\u8001\u5E08\u5BF9\u5988\u5988\u8BF4.</p><p>&quot;\u90A3\u6211\u8D70\u4E86&quot;,\u5988\u5988\u770B\u7740\u6211,\u4F3C\u4E4E\u5728\u62C5\u5FC3\u4EC0\u4E48,\u7136\u540E\u8F6C\u8EAB\u79BB\u5F00\u4E86.</p><p>\u6211\u4E00\u8138\u832B\u7136,\u4F46\u8FD8\u662F\u5047\u88C5\u9547\u5B9A\u7684\u770B\u7740\u5988\u5988\u7684\u80CC\u5F71\u6E10\u884C\u6E10\u8FDC.</p><p>\u7B49\u5988\u5988\u7684\u80CC\u5F71\u5B8C\u5168\u6D88\u5931\u4E0D\u89C1,\u6211\u6123\u4E86\u4E00\u4F1A\u513F,\u7A81\u7136\u611F\u89C9\u8FD9\u4E2A\u4E16\u754C\u597D\u964C\u751F,\u5F00\u59CB\u54C7\u54C7\u5927\u54ED.</p><p>\u8001\u5E08\u770B\u5230\u6211\u8FD9\u6837,\u5C31\u5E26\u6211\u8D70\u8FDB\u4E86\u6559\u5BA4.</p><h2 id="\u5728\u6559\u5BA4\u91CC" tabindex="-1"><a class="header-anchor" href="#\u5728\u6559\u5BA4\u91CC" aria-hidden="true">#</a> \u5728\u6559\u5BA4\u91CC</h2><p>\u6559\u5BA4\u91CC\u6EE1\u662F\u5C0F\u5B69\u5728,\u90FD\u5728\u81EA\u5DF1\u7684\u5EA7\u4F4D\u4E0A\u568E\u5555\u5927\u54ED,\u8001\u5E08\u6307\u4E86\u6307\u524D\u9762\u7684\u7A7A\u4F4D\u5B50,&quot;\u4F60\u5750\u5728\u90A3\u91CC\u5427&quot;.</p><p>\u968F\u540E\u8001\u5E08\u5728\u95E8\u524D\u653E\u4E86\u4E00\u628A\u6905\u5B50,\u81EA\u5DF1\u505A\u7740\u6905\u5B50\u4E0A\u9876\u7740\u95E8,\u6015\u5C0F\u5B69\u4EEC\u8DD1\u51FA\u53BB.</p><p>\u6211\u6162\u541E\u541E\u7684\u8D70\u5230\u81EA\u5DF1\u7684\u5EA7\u4F4D\u4E0A,\u773C\u91CC\u7684\u6CEA\u6C34\u5728\u75AF\u72C2\u6253\u8F6C,\u54C7\u7684\u4E00\u58F0\u53C8\u54ED\u4E86\u51FA\u6765.</p><p>&quot;\u4E00\u4F1A\u5988\u5988\u5E72\u5B8C\u6D3B\u5C31\u6765\u63A5\u4F60\u4E86,\u4F60\u5148\u5728\u8FD9\u91CC\u8DDF\u540C\u5B66\u4EEC\u719F\u6089\u719F\u6089&quot;,\u8001\u5E08\u8BF4\u9053.</p><p>\u6211\u54ED\u7740\u4ECE\u6211\u7684\u5EA7\u4F4D\u4E0A\u51FA\u6765,\u8D70\u5230\u8001\u5E08\u7684\u8DDF\u524D.</p><p>&quot;\u522B\u54ED\u4E86,\u4E56,\u5988\u5988\u4E00\u4F1A\u5C31\u6765\u4E86,\u4E00\u4F1A\u513F\u7ED9\u4F60\u4E70\u7CD6\u5403&quot;,\u8001\u5E08\u5B89\u6170\u6211\u8BF4.</p><p>&quot;\u545C,\u6211\u4E0D\u54ED\u4E86,\u8001\u5E08\u4F60\u4E5F\u522B\u54ED\u4E86&quot;,\u6211\u4E0D\u77E5\u4ECE\u54EA\u91CC\u5192\u51FA\u6765\u8FD9\u6837\u4E00\u53E5\u8BDD.</p><p>&quot;\u597D\u597D\u597D,\u6211\u4E5F\u4E0D\u54ED\u4E86,\u4F60\u56DE\u5EA7\u4F4D\u4E0A\u53BB\u5427&quot;,\u8001\u5E08\u6478\u4E86\u6478\u6211\u7684\u5934,\u7B11\u7740\u5BF9\u6211\u8BF4.</p><p>\u6211\u56DE\u5230\u5EA7\u4F4D\u4E0A,\u770B\u7740\u5468\u56F4\u964C\u751F\u7684\u540C\u5B66,\u7D22\u6027\u8DB4\u5728\u684C\u5B50\u4E0A,\u542C\u7740\u4ED6\u4EEC\u9707\u8033\u6B32\u804B\u7684\u54ED\u58F0,\u81EA\u5DF1\u8FF7\u8FF7\u7CCA\u7684.</p><h2 id="\u653E\u5B66\u540E" tabindex="-1"><a class="header-anchor" href="#\u653E\u5B66\u540E" aria-hidden="true">#</a> \u653E\u5B66\u540E</h2><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45,\u54ED\u58F0\u4E5F\u8D8A\u6765\u8D8A\u5C0F\u4E86,\u6559\u5BA4\u91CC\u7684\u95E8\u7A81\u7136\u6253\u5F00,\u5BB6\u957F\u4EEC\u90FD\u5728\u5916\u9762\u63A5\u5B69\u5B50\u56DE\u5BB6.</p><p>\u968F\u7740\u6559\u5BA4\u91CC\u7684\u5B69\u5B50,\u8D8A\u6765\u8D8A\u5C11,\u6211\u4E5F\u8D8A\u6765\u8D8A\u5BB3\u6015,\u653E\u751F\u75DB\u54ED\u8D77\u6765,\u8001\u5E08\u5728\u95E8\u53E3\u628A\u5B69\u5B50\u4EEC\u4E00\u4E2A\u4E00\u4E2A\u9001\u56DE\u53BB,\u6700\u540E\u53EA\u5269\u4E0B\u4E86\u6211\u4EEC\u4E09\u56DB\u4E2A.</p>',29);function e(u,r){return a}var d=t(o,[["render",e],["__file","\u7B2C\u4E00\u6B21\u53BB\u5B66\u6821.html.vue"]]);export{d as default};
